package com.si.sample;

public class Conversion {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        byte a = 127;
        System.out.println(a);
        short b = a;
        System.out.println(b);
        byte c = (byte)b;
        System.out.println(c);
        String M = "mani";
        System.out.println(M);
        
	}

}
